mapper.update:
update post
set post_collectnum = post_collectnum + 1
where post_id = #{id};

update post
set post_collectnum = post_collectnum - 1
where post_id = #{id};

update post
set post_commentnum = post_commentnum + 1
where post_id = #{id};

update post
set post_commentnum = post_commentnum - 1
where post_id = #{id};

update post
set post_readNum = post_readNum + 1
where post_id = #{id};

update post
set post_readNum = post_readNum - 1
where post_id = #{id};
